export = DebugScreenshotsProvider;
/**
 * Interface for saving debug screenshots!
 * @abstract
 */
declare class DebugScreenshotsProvider {
    _prefix: string;
    _path: string;
    /**
     * @return {string}
     */
    getPrefix(): string;
    /**
     * @param {string} value
     */
    setPrefix(value: string): void;
    /**
     * @return {string}
     */
    getPath(): string;
    /**
     * @param {string} value
     */
    setPath(value: string): void;
    /**
     * @abstract
     * @param {MutableImage} image
     * @param {string} suffix
     * @return {Promise}
     */
    save(_image: any, _suffix: any): Promise<any>;
}
